    override fun addGoodsToCart(goods: Goods, quantity: Int): Completable {
        val goodsCart = Cart(goods, quantity)
        return DBHelper.instance.getCartDao().insertOrUpdate(goodsCart, quantity)
    }

    override fun fetchCartCount(): Flowable<Int> {
        return DBHelper.instance.getCartDao().getSumOfQuantity()
    }

    override fun fetchCart(): Flowable<List<Cart>> {
        return DBHelper.instance.getCartDao().getAll()
    }

    override fun deleteAllGoods(): Completable {
        return DBHelper.instance.getCartDao().deleteAll()
    }

    override fun deleteGoods(id: String): Completable {
        return DBHelper.instance.getCartDao().deleteByID(id)
    }

    override fun updateGoods(cart: Cart): Completable {
        return DBHelper.instance.getCartDao().update(cart)
    }
}